mapper.select:
WITH RECURSIVE organization_tree AS ( SELECT id, parent_id, name, code, enabled, remark, sort_order, root_id FROM organization WHERE id IN
#{id}
UNION ALL SELECT o.id, o.parent_id, o.name, o.code, o.enabled, o.remark, o.sort_order, o.root_id FROM organization o INNER JOIN organization_tree ot ON o.parent_id = ot.id ) SELECT * FROM organization_tree ORDER BY sort_order ASC, id ASC

SELECT * FROM organization


    AND id = #{organization.id}


    AND parent_id = #{organization.parentId}


    AND enabled = #{organization.enabled}


    AND code LIKE CONCAT('%', #{organization.code}, '%')


    AND name LIKE CONCAT('%', #{organization.name}, '%')


ORDER BY sort_order ASC, id ASC
